import React, { Component } from "react";
import axios from "axios";
import { Nav } from "./Nav";
import {Footer} from "./Footer";

import "../components/css/Mainpage.css";
export class Mainpage extends Component {

  constructor(props) {
    super(props);
    this.state = {
      selectedFile: [],
      filename: ''
    };
    // localStorage.clear();
    if(localStorage.getItem('firstname') == null){
      this.isloggedin = false;
    }else{
      this.isloggedin = true;
    }
    this.handleselectedFile = this.handleselectedFile.bind(this);
    this.handleUpload = this.handleUpload.bind(this);
    this.handleSearch = this.handleSearch.bind(this);
    this.captureInput = this.captureInput.bind(this);
  }

  handleUpload(e) {
    e.preventDefault();
    const fd = new FormData();
    if(this.state.filename === ''){
      console.log('this was a mandatory input');
      return;
    }else{
     // creating the form data with the name that can be identified at the server
      fd.append('musicUpload',this.state.selectedFile,this.state.filename);
      console.log(this.state.selectedFile); 
    }
    //========================================
    axios
      .post("http://localhost:8080/api/uploads", fd)
      .then(result => {
        console.log(result);
        if (result.data.errors) {
          return this.setState(result.data);
        }
      });
  }

  handleselectedFile(e) {
    this.setState({
      selectedFile: e.target.files[0],
    });
    // let url = "http://localhost:8080/api/files"+filename;
    // axios
    //   .get("http://localhost:8080/api/files")
    //   .then();
  }

  captureInput(e){
    this.setState({
      filename: e.target.value
    });
  }

  handleSearch(e){
    let filename = this.state.filename;
    let url = "http://localhost:8080/api/files/id:"+filename;
    axios
      .post(url)
      .then(result=>{

      });
  }

  //fetch music uploaded by a person and for loop through the filenames
  render() {
    return this.isloggedin ? (
      <div className="body5">
        <Nav login={this.isloggedin}/>
        <div id="mainpageshow" >
        <div className="bg1" />
          <h1>FACE THE MUSIC!!</h1>
          <div className="Searchbar">
          <input type="text" className="searchinput" name="filename" onChange = {this.captureInput} placeholder="Search for your music"></input>
          <button onClick={this.handleSearch} className="searchbtn">Search</button>
          </div>
          <input
            onChange = {this.handleselectedFile}
            type="file"
            name="post"
            id="post"
            className="form-group"
          />
          <br>
          </br>
          <div className="input2">
          <input
            onChange = {this.captureInput}
            type="text"
            className="form-group1"
            placeholder="Name your file"/>
          {/* //take name of file so you can send in form data */}
          
          <button onClick={this.handleUpload} className="mainpagebutton">Upload</button></div>
          {/* {} */}
          <br/>
      </div>
      <Footer />
    </div>
    ) : (
      <div>
      <Nav/>
      <h3>Please login</h3>
      <Footer/>
      </div>
    );
  }
}
export default Mainpage;
