import React, { Component } from "react";
import axios from "axios";
import { withRouter } from "react-router-dom";
import { Nav } from "./Nav";

//using export directly with class to export it instead of export default app
// do read documentation on https://reactjs.org/docs/forms.html
export class Register extends Component {
  constructor(props) {
    super(props);
    //initialize props/elements
    this.state = {
      username: "",
      email: "",
      firstname: "",
      lastname: "",
      password: "",
      password_con: "",
      userdata: null,
      success: false
    };
    this.isloggedin = false;
    //bind events or basically teach functions to read current state
    this.changeHandler = this.changeHandler.bind(this);
    this.submitHandler = this.submitHandler.bind(this);
    this.redirection = this.redirection.bind(this);
  }

  changeHandler(e) {
    this.setState({
      [e.target.name]: e.target.value
    });
  }

  submitHandler(e) {
    e.preventDefault();
    //always match the post/get request method with routes.js post/get
    axios
      .post("http://localhost:8080/api/register", this.state)
      .then(result => {
        console.log(result);
        if (result.data.errors) {
          return this.setState(result.data);
        }else{
           
          this.setState({
            userdata: result.data,
            errors: null,
            success: true
          });

          this.redirection(this.state);
          return this.state;
        }
      });
  }

  redirection(e){
    console.log(this.state.success);
    if(this.state.success){
      this.props.history.push("/Mainpage");
    }
  }

  render() {
    return (
       <div>
         <Nav login={this.isloggedin}/> 
        {this.state.success && <p>You are successfully registerated!</p>}
        <form onSubmit={this.submitHandler}>
        <label>First Name:</label>
        <input
            type="text"
            onChange={this.changeHandler}
            placeholder="First Name"
            name="firstname"
            id="firstname"
          />
          {this.state.errors &&
            this.state.errors.firstname && (
              <p>{this.state.errors.firstname.msg}</p>
            )}
          <br />
          <label>Last Name:</label>
          <input
            type="text"
            onChange={this.changeHandler}
            placeholder="Last Name"
            name="lastname"
            id="lastname"
          />
          {this.state.errors &&
            this.state.errors.lastname && (
              <p>{this.state.errors.lastname.msg}</p>
            )}
          <br />
          <label>Email:</label>
          <input
            type="email"
            placeholder="Email address"
            name="email"
            onChange={this.changeHandler}
            id="emailreg"
          />
          {this.state.errors &&
            this.state.errors.email && <p>{this.state.errors.email.msg}</p>}
          <br />
          <label>Password:</label>
          <input
            type="password"
            onChange={this.changeHandler}
            placeholder="Password"
            name="password"
            id="passwordreg"
          />
          {this.state.errors &&
            this.state.errors.password && (
              <p>{this.state.errors.password.msg}</p>
            )}
          <br />
          <label>Reconfirm password:</label>
          <input
            type="password"
            onChange={this.changeHandler}
            placeholder="Re-confirm password"
            name="password_con"
            id="password_con"
          />
          {this.state.errors &&
            this.state.errors.password_con && (
              <p>{this.state.errors.password_con.msg}</p>
            )}
          <br />
          <br />
          <button type="Submit">Submit</button>
        </form>
      </div>
    );
  }
}

export default withRouter(Register);
