export class AboutUs extends Component {
  constructor(props){
    super(props);
    if(localStorage.getItem('firstname') == null){
      this.isloggedin = false;
    }else{
      this.isloggedin = true;
    }
  }
  render() {
    return (
      <div>
      <Nav login={this.isloggedin}/>
      {/* <header id="aboutus" className="grid">
        <div className="bg-about" />
