import React, { Component } from "react";
import { withRouter } from "react-router-dom";
import { Nav } from "./Nav";
import './Home.css';


export class Home extends Component{
  constructor(props){
    super(props);
    localStorage.clear();
    if(localStorage.getItem('firstname') == null){
      this.isloggedin = false;
    }else{
      this.isloggedin = true;
    }
  }

  render(){
    return(
      <div>
        <Nav login={this.isloggedin}/>
        <header id="showcase" className="grid">
          <div className="bg-image" />
          <div className="content-wrap">
            <h1 className="animate-reveal animate-first">WebDesign Alpha</h1>
            <p className="animate-reveal animate-second">Lorem ipsum, dolor sit amet consectetur adipisicing elit. Adipisci eum error earum soluta voluptatum nisi laboriosam eos saepe asperiores dolorum.</p>
            <a href="#section-b" className="btn animate-reveal animate-third">Read More</a>
          </div>
        </header>
        {/* Main Area */}
        <div id="main">
          {/* Section A */}
          <section id="section-a" className="grid">
            <div className="content-wrap">
              <h2 className="content-title">Web &amp; Application Development</h2>
              <div className="content-text">
                <p>Lorem ipsum dolor sit amet consectetur adipisicing elit. Saepe sint eligendi possimus? Unde officiis magnam laborum ipsa distinctio odio, vero dolores dicta aliquam aperiam repellendus. Perferendis officiis deserunt velit voluptas nobis sequi
                  animi totam, accusantium, ex eius quia, natus quo?</p>
              </div>
            </div>
          </section>
          <div>
<button type="button" className="btn btn-primary btn-lg" data-toggle="modal" data-target="#myModal">
  Launch demo modal
</button>

<div className="modal fade" id="myModal" tabindex="-1" role="dialog" aria-labelledby="myModalLabel" aria-hidden="true">
  <div className="modal-dialog">
    <div className="modal-content">
      <div className="modal-header">
        <button type="button" className="close" data-dismiss="modal"><span aria-hidden="true">&times;</span><span className="sr-only">Close</span></button>
        <h4 className="modal-title" id="myModalLabel">Modal title</h4>
      </div>
      <div className="modal-body">
        ...
      </div>
      <div className="modal-footer">
        <button type="button" className="btn btn-default" data-dismiss="modal">Close</button>
        <button type="button" className="btn btn-primary">Save changes</button>
      </div>
    </div>
  </div>
</div>
        </div>
          
        </div>
      </div>
    )
  }
}
export default withRouter(Home);
