import React, { Component } from "react";
import { withRouter } from "react-router-dom";
import { Nav } from "./Nav";

export class ContactUs extends Component{
  constructor(props){
    super(props);
    if(localStorage.getItem('firstname') == null){
      this.isloggedin = false;
    }else{
      this.isloggedin = true;
    }
  }

  render(){
    return(
    <div className="contact-us">
    <Nav login={this.isloggedin}/>
    <div className="jumbotron" id="jumbotron1">
    <img src={Image1} alt="boston1"/>
    <h2 className="contacth2">CONTACT US</h2>
    </div>
   <div id="contact" className="container">
   <h3 className="text-center">Contact</h3>

   <div className="row">

     <div className="">
       <div className="row">
         <div className="col-sm-12 form-group">
           <input className="form-control" id="name" name="name" placeholder="Name" type="text" required />
         </div>
         <div className="col-sm-12 form-group">
           <input className="form-control" id="email" name="email" placeholder="Email" type="email" required />
         </div>
       </div>
       <div className="row">
         <div className="col-sm-12 form-group">
           <input className="form-control" id="name" name="phoneno" placeholder="PhoneNo" type="text" required />
         </div>

       </div>
       <textarea className="form-control" id="comments" name="comments" placeholder="Comment" rows="5"></textarea>
       <br />
       <div className="row">
         <div className="col-md-12 form-group">
           <button className="btn1" type="submit">Send</button>
         </div>
       </div>
     </div>
     <br/>
         {/* <div className="span">
       <p><span className="glyphicon glyphicon-map-marker"></span>Boston, US</p>
       <p><span className="glyphicon glyphicon-phone"></span>Phone: +1 999999999</p>
       <p><span className="glyphicon glyphicon-envelope"></span>Email: email@email.com</p>
     </div> */}
   </div>
   <br />

 </div>
 </div>);
  }
}
export default withRouter(ContactUs);
