import React, { Component } from "react";
import { withRouter } from "react-router-dom";
import { Nav } from "./Nav";
import '../components/css/Home.css';
import {Footer} from "./Footer";

export class Home extends Component{
  constructor(props){
    super(props);
    localStorage.clear();
    if(localStorage.getItem('firstname') == null){
      this.isloggedin = false;
    }else{
      this.isloggedin = true;
    }
  }

  render(){
    return(
      // <div>
<div className="Homepage">
      <Nav login={this.isloggedin}/>
      
      <div id="demo" className="carousel slide" data-ride="carousel">
        {/* Indicators */}
        <ul className="carousel-indicators">
          <li data-target="#demo" data-slide-to={0} className="active" />
          <li data-target="#demo" data-slide-to={1} />
          <li data-target="#demo" data-slide-to={2} />
        </ul>
        {/* The slideshow */}
        <div className="carousel-inner">
          <div className="carousel-item active">
            <img src="musica.jpg" alt="Los Angeles" width={1100} height={500} />
          </div>
          <div className="carousel-item">
            <img src="musica.jpg" alt="Chicago" width={1100} height={500} />
          </div>
          <div className="carousel-item">
            <img src="musica.jpg" alt="New York" width={1100} height={500} />
          </div>
        </div>
        {/* Left and right controls */}
        <a className="carousel-control-prev" href="#demo" data-slide="prev">
          <span className="carousel-control-prev-icon" />
        </a>
        <a className="carousel-control-next" href="#demo" data-slide="next">
          <span className="carousel-control-next-icon" />
        </a>
      </div>



      {/* <div id="homecarousel" className="carousel slide" data-ride="carousel">
        <ul className="carousel-indicators">
          <li data-target="#homecarousel" data-slide-to={0} className="active" />
          <li data-target="#homecarousel" data-slide-to={1} />
          <li data-target="#homecarousel" data-slide-to={2} />
          
        </ul>
        <div className="carousel-inner">
        <div className="carousel-caption">
              <h1 className="animate-reveal animate-first">AMUSIA!</h1>
           <p className="animate-reveal animate-second">A new music streaming service</p>
             <a href="/Login" className="btn animate-reveal animate-third">Login to Begin!</a>
      </div>
          <div className="carousel-item active">
            <div className="bg3" />
              <img alt=""/>
          </div>
          <div className="carousel-item">
          <div className="bg" />
            <img alt=""/>
          </div>
          <div className="carousel-item">
          <div className="bg2" />
            <img alt=""/>
          </div>
        </div>
        <a className="carousel-control-prev" href="#homecarousel" data-slide="prev">
          <span className="carousel-control-prev-icon" />
        </a>
        <a className="carousel-control-next" href="#homecarousel" data-slide="next">
          <span className="carousel-control-next-icon" />
        </a>
      </div>
      </div> */}
       {/* <Footer />  */}
      </div>
    )
  }
}
export default withRouter(Home);
