import React, { Component } from "react";
import { withRouter } from "react-router-dom";
import { Nav } from "./Nav";
 import Image1 from "./boston.jpg";
import "./Contact.css";
export class ContactUs extends Component{
  constructor(props){
    super(props);
    if(localStorage.getItem('firstname') == null){
      this.isloggedin = false;
    }else{
      this.isloggedin = true;
    }
  }

  render(){
    return(
    <div className="contact-us">
    <Nav login={this.isloggedin}/>
    <header id="showcase" >
      <h2 className="contacth2">CONTACT US</h2>
      </header>
    <div className="section-center">
			<div className="container"> <h2 className="contacth2">CONTACT US</h2><h1>Just say hello!</h1>
				<div className="row">
          <div className="col-md-7 col-md-push-5">
	          <div className="booking-cta">
		          <p>Feel free to get in touch with us. We are open to creative ideas, projects and new opportunities</p>
              <p>Boston, US<br/>Phone: +1 999999999<br/>Email: email@email.com</p>
            </div>
          </div>
          <div className="col-md-4 col-md-pull-7">
		        <div className="booking-form">
			        <form>
				        <div className="form-group">
					        <input className="form-control" type="text" placeholder="Name"/>
				        </div>
                <div className="form-group">
					        <input className="form-control" type="text" placeholder="Email"/>
				        </div>
                <div className="form-group">
				        	<input className="form-control" type="text" placeholder="PhoneNumber"/>
			        	</div>
                <div className="form-group">
					        <input className="form-control" type="text" placeholder="Queries"/>
				        </div>
				        <div className="form-btn">
						      <button className="submit-btn">Submit</button>
				        </div>
			        </form>
		        </div>
	        </div>
        </div>
      </div>
		</div>
	</div>
);
  }
}
export default withRouter(ContactUs);
