import React from 'react';
import { withRouter } from "react-router-dom";
import "./css/Nav.css"

const NavItem = props => {
  const pageURI = window.location.pathname+window.location.search
  const liClassName = (props.path === pageURI) ? "nav-item active" : "nav-item";
  const aClassName = props.disabled ? "nav-link disabled" : "nav-link"
  return (
    <li className={liClassName}>
      <a href={props.path} className={aClassName}>
        {props.name}
        {(props.path === pageURI) ? (<span className="sr-only">(current)</span>) : ''}
      </a>
    </li>
  );
}



export class Nav extends React.Component {
  
  render() {
    return this.props.login ? (
      <nav id="navhome" className="navbar navbar-expand-lg navbar-light bg-light ">
        <a className="navbar-brand" href="/UserHome">Amusia</a>
        <div className="collapse navbar-collapse" id="navbarSupportedContent">
          <ul className="navbar-nav mr-auto">
            <NavItem path="/aboutus" name="About" />
            <NavItem path="/MainPage" name="Stream Music"/>
            <NavItem path="/blog" name="Blog" />
            <NavItem path="/careers" name="Careers"/>
            <NavItem path="/contactus" name="Contact"/>
            <NavItem path="/" name="Log out"/>
          </ul>
        </div>  
      </nav>
      

    ) :(
      // <nav id="navba" className="navbar navbar-expand-lg navbar-light bg-light ">
      //   <a className="navbar-brand" href="/">Amusia</a>
      //   <div className="collapse navbar-collapse" id="navbarSupportedContent">
      //     <ul className="navbar-nav mr-auto">
      //       <NavItem path="/aboutus" name="About" />
      //       <NavItem path="/careers" name="Careers"/>
      //       <NavItem path="/contactus" name="Contact"/>
      //       <NavItem path="/login" name="Login" />
      //     </ul>

      //   </div>
      // </nav>

      <Navbar color="indigo" dark expand="md">
      <NavbarBrand>
        <strong className="white-text">Navbar</strong>
      </NavbarBrand>
      <NavbarToggler
        onClick={this.toggleCollapse}
      />
      <Collapse
        id="navbarCollapse3"
        isOpen={this.state.isOpen}
        navbar
      >
        <NavbarNav left>
          <NavItem active>
            <NavLink to="#!">Home</NavLink>
          </NavItem>
          <NavItem>
            <NavLink to="#!">Features</NavLink>
          </NavItem>
          <NavItem>
            <NavLink to="#!">Pricing</NavLink>
          </NavItem>
          <NavItem>
            <Dropdown>
              <DropdownToggle nav caret>
                <div className="d-none d-md-inline">Dropdown</div>
              </DropdownToggle>
              <DropdownMenu right>
                <DropdownItem href="#!">Action</DropdownItem>
                <DropdownItem href="#!">Another Action</DropdownItem>
                <DropdownItem href="#!">Something else here</DropdownItem>
                <DropdownItem href="#!">Something else here</DropdownItem>
              </DropdownMenu>
            </Dropdown>
          </NavItem>
        </NavbarNav>
        <NavbarNav right>
          <NavItem>
            <FormInline waves>
              <div className="md-form my-0">
                <input
                  className="form-control mr-sm-2"
                  type="text"
                  placeholder="Search"
                  aria-label="Search"
                />
              </div>
            </FormInline>
          </NavItem>
        </NavbarNav>
      </Collapse>
  </Navbar>
    );
  }
}

export default withRouter(Nav);
