import React, { Component } from 'react';
// import '../components/css/FAQs.css';
import { Nav } from "./Nav";
import Footer from './Footer';
import axios from 'axios';

import abstract2 from '../images/abstract2.jpg';
export class Playlist extends Component{
    constructor(props){
        super(props);
        this.playlist= null;
        if(localStorage.getItem('firstname') == null){
            this.isloggedin = false;
          }else{
            this.isloggedin = true;
          }
        this.state={
            filearray : []
        }
        this.value='';
    }

    componentWillMount(){
        let forf = "testest";
        console.log("component called");
        let url= "http://localhost:8080/music/show/"+localStorage.getItem('firstname')+"/"+forf;
          axios
            .get(url)
            .then(result=>{
                if(result.data.errors){
                    return console.log(result.data.errors);
                }else{
                    if(result.data){
                        console.log(JSON.stringify(result.data));
                        // this.setState = { filearray : JSON.stringify(result.data)};
                        this.setState({filearray:result.data});
                        // this.value = this.state.filearray[0];
                        console.log(result.data);
                    }
                }
            })
            .catch(function (error) {
                console.log(error);
            });
    }

    render(){
        // return this.isloggedin && this.state.filearray !==null ?
        const filearrayL = this.state.filearray;
        // console.log(filearrayL);
        let valarray='';
        if(filearrayL.length >0){
            console.log(filearrayL.length);
            valarray = filearrayL.map(obj=>{
                return (
                    <div key={obj._id}>
                        <label>{obj.filename}</label><br></br>
                    <audio controls><source src={obj.filepath} /><br></br></audio>
                    </div>
                )
            })
        }
        return(
            <div>
                <Nav login={this.isloggedin}/>
            <div className="Playlist">
                {valarray}
            </div>
            <section id="section-d" className="grid">
        <div className="box">
          <h2 className="content-title">Contact Us</h2>
          <p>As the worldâ€™s largest music and audio platform, Amusia lets people discover and 
  enjoy the greatest selection of music from the most diverse creator community on earth.
  The platform has become renowned for its unique content and features, including the ability 
  to share music and connect directly with artists, as well as unearth breakthrough tracks, 
  raw demos, podcasts and more. This is made possible by an open platform that directly 
  connects creators and their fans across the globe. Music and audio creators use Amusia
  to both share and monetise their content with a global audience, as well as receive detailed 
  stats and feedback from the Amusia community.</p>
        </div>
        <div className="box" id="boximg" >  
         <img src={abstract2} className="imageblog" alt="" style={{width:'100%'}} />        
        </div>
      </section>
            <Footer/>
            </div>
        );
    }
}
